mapper.insert:
INSERT INTO customer (
    id, name, contact_person, phone, email, address, type, status, created_at, updated_at
) VALUES (
    #{po.id}, #{po.name}, #{po.contactPerson}, #{po.phone}, #{po.email},
    #{po.address}, #{po.type}, #{po.status}, #{po.createdAt}, #{po.updatedAt}
)

mapper.update:
UPDATE customer
name = #{po.name}, contact_person = #{po.contactPerson}, phone = #{po.phone}, email = #{po.email}, address = #{po.address}, type = #{po.type}, status = #{po.status}, updated_at = #{po.updatedAt}
WHERE id = #{po.id}
